mapper.update:
UPDATE t_link
SET total_pv  = total_pv + #{totalPv},
    total_uv  = total_uv + #{totalUv},
    total_uip = total_uip + #{totalUip}
WHERE gid = #{gid}
  AND full_short_url = #{fullShortUrl}

mapper.select:
SELECT t.*, COALESCE(s.today_pv, 0) AS todayPv, COALESCE(s.today_uv, 0) AS todayUv, COALESCE(s.today_uip, 0) AS todayUip FROM t_link t LEFT JOIN t_link_stats_today s ON t.full_short_url = s.full_short_url AND s.date = CURDATE() WHERE t.gid IN
#{item}
AND t.enable_status = 1 AND t.del_flag = 0 ORDER by t.update_time
